mapper.select:
select  LEC_CODE, COU_CODE, LEC_NAME, LEC_CONTENT, VIDEO_NAME
from	LECTURES
where	cou_code = #{couCode}

select 	CR_COUNT, COMPLETION_STATUS
from 	COURSE_REGISTRATION
where	ui_Id = #{uiId} and cou_code = #{couCode}

mapper.update:
update courses 
set approve='Y'
where cou_code=#{couCode}
